document.addEventListener("DOMContentLoaded",function(){const dialog=document.getElementById("modal-dialog");const closeButton=document.getElementById("close-btn");closeButton.addEventListener("click",function(){closeDialog()});window.addEventListener("keydown",function(event){if(event.key==="Escape"&&dialog.open){closeDialog()}});const now=new Date;const today=now.toDateString();var lastShownDate=localStorage.getItem("modalLastShownDate");if(lastShownDate===null||lastShownDate!==today){localStorage.setItem("modalLastShownDate",today);dialog.showModal();document.body.classList.add("modal-open")}function closeDialog(){dialog.close();document.body.classList.remove("modal-open")}});